mapper.update:
update face_feature
set create_time = #{createTime,jdbcType=TIMESTAMP},
  image_url = #{imageUrl,jdbcType=VARCHAR},
  feature = #{feature,jdbcType=LONGVARBINARY}
where fid = #{fid,jdbcType=BIGINT}

mapper.select:
select fid, create_time, image_url, feature
from face_feature
where fid = #{fid,jdbcType=BIGINT}
