mapper.update:
update face_feature
set create_time = #{createTime,jdbcType=TIMESTAMP},
  feature = #{feature,jdbcType=LONGVARBINARY},
  image = #{image,jdbcType=LONGVARBINARY}
where fid = #{fid,jdbcType=BIGINT}

mapper.select:
select fid, create_time, feature, image
from face_feature
where fid = #{fid,jdbcType=BIGINT}
