set name = #{name,jdbcType=VARCHAR},
  idnumber = #{idnumber,jdbcType=VARCHAR},
  phone = #{phone,jdbcType=VARCHAR},
  creat_time = #{creatTime,jdbcType=TIMESTAMP}
